import React, { useState, useEffect } from 'react';
import { Carousel } from 'primereact/carousel';
import "./Roomtypepage.scss";
const responsiveOptions = [
    {
        breakpoint: '1024px',
        numVisible: 3,
        numScroll: 3
    },
    {
        breakpoint: '600px',
        numVisible: 2,
        numScroll: 2
    },
    {
        breakpoint: '480px',
        numVisible: 1,
        numScroll: 1
    }
];

const Roomtypepage = (props) => {
    const { selectedCard } = props;
    const [imagesCount, setImagesCount] = useState();
    useEffect(() => {
        let images = [];
        for (let i = 0; i < selectedCard.count; i++) {
            let index = i;
            images.push({
                image: index + 1
            })

        }
        setImagesCount(images)

    }, [selectedCard.subImagesBaseUrl]);

    const productTemplate = (item) => {
        return(
            <div className="product-item">
                <div className="product-item-content">
                <div className="p-mb-3">
                <img src={require(`../../assets/images/${selectedCard.subImagesBaseUrl}/${item.image}.png`)} />
                </div>
                </div>
                </div>
        )
    }
    console.log(imagesCount,"imagesCount")
    return (
        <div>
            <div className="card">
              {imagesCount && imagesCount.length &&  <Carousel value={imagesCount} numVisible={2} numScroll={1}   className="custom-carousel" 
                    circular autoplayInterval={2000}  itemTemplate={productTemplate} header={<h5>Circular, AutoPlay, 3 Items per Page and Scroll by 1</h5>} />}
            </div>
            {
                imagesCount && imagesCount.map((item)=>{
                    productTemplate
                })
            }
        </div>
    )
};
export default Roomtypepage;
