import Slider from "react-slick";
const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
const Roomtypepage = (props) => {
    const { selectedCard } = props;
    const [imagesCount, setImagesCount] = useState();
    useEffect(() => {
        let images = [];
        for (let i = 0; i < selectedCard.count; i++) {
            let index = i;
            images.push({
                image: index + 1
            })

        }
        setImagesCount(images)

    }, [selectedCard.subImagesBaseUrl]);

    return (
      <div>
        <h2> Single Item</h2>
        <Slider {...settings}>
          {imagesCount && imagesCount.map((item)=>{
                    return (<img src={require(`../../assets/images/${selectedCard.subImagesBaseUrl}/${item.image}.png`)} />)

          })}
        </Slider>
      </div>
    );
  }

export default Roomtypepage;
